import React, { useState, useEffect } from "react";
import "./index.css";

function App() {
  const [students, setStudents] = useState(() => {
    const saved = localStorage.getItem("students");
    return saved ? JSON.parse(saved) : [];
  });
  const [inputNames, setInputNames] = useState("");

  useEffect(() => {
    localStorage.setItem("students", JSON.stringify(students));
  }, [students]);

  const addStudents = () => {
    if (inputNames.trim() === "") return;
    const newStudents = inputNames
      .split(",")
      .map((name) => name.trim())
      .filter((name) => name !== "")
      .map((name) => ({
        name,
        absences: [],
        fee: 60,
      }));
    setStudents([...students, ...newStudents]);
    setInputNames("");
  };

  const markAbsent = (index) => {
    const date = new Date().toLocaleDateString();
    const updated = [...students];
    updated[index].absences.push(date);
    updated[index].fee = Math.max(0, updated[index].fee - 10);
    setStudents(updated);
  };

  const resetAll = () => {
    localStorage.removeItem("students");
    setStudents([]);
  };

  return (
    <div className="min-h-screen bg-green-900 text-white p-6">
      <h1 className="text-3xl font-bold text-center mb-6 animate-pulse">
        Masaru Team Attendance System
      </h1>

      <div className="flex justify-center mb-6">
        <input
          className="p-2 rounded-l-lg text-black w-1/2"
          type="text"
          value={inputNames}
          onChange={(e) => setInputNames(e.target.value)}
          placeholder="Въведи имена, разделени със запетая"
        />
        <button
          className="bg-yellow-500 px-4 py-2 rounded-r-lg hover:bg-yellow-600 transition-all duration-300 transform hover:scale-105"
          onClick={addStudents}
        >
          Добави
        </button>
      </div>

      <div className="grid md:grid-cols-2 lg:grid-cols-3 gap-4">
        {students.map((student, index) => (
          <div
            key={index}
            className="bg-green-800 rounded-2xl shadow-lg p-4 hover:scale-105 transition transform duration-300"
          >
            <h2 className="text-xl font-semibold mb-2">{student.name}</h2>
            <p>Отсъствия: {student.absences.length}</p>
            <p>Такса: {student.fee} лв</p>

            {student.absences.length > 0 && (
              <div className="mt-2 text-sm text-gray-300">
                <p>Дати на отсъствия:</p>
                <ul className="list-disc list-inside">
                  {student.absences.map((date, i) => (
                    <li key={i}>{date}</li>
                  ))}
                </ul>
              </div>
            )}

            <button
              className="bg-red-500 px-3 py-1 rounded hover:bg-red-600 mt-3 transition-all duration-300 transform hover:scale-105"
              onClick={() => markAbsent(index)}
            >
              Отсъствал
            </button>
          </div>
        ))}
      </div>

      <div className="flex justify-center mt-8">
        <button
          className="bg-gray-700 px-4 py-2 rounded hover:bg-gray-800 transition-all duration-300 transform hover:scale-105"
          onClick={resetAll}
        >
          Нулирай всичко
        </button>
      </div>

      <p className="text-sm text-gray-400 text-center mt-6">
        Made by Teodor Popovski • Member of Masaru Team
      </p>
    </div>
  );
}

export default App;
