// src/App.js
import React, { useState, useEffect } from "react";
import * as XLSX from "xlsx";
import { saveAs } from "file-saver";
import "./index.css";

export default function App() {
  const [input, setInput] = useState("");
  const [month, setMonth] = useState("Октомври");
  const [students, setStudents] = useState([]);
  const [search, setSearch] = useState("");
  const months = [
    "Януари","Февруари","Март","Април","Май","Юни",
    "Юли","Август","Септември","Октомври","Ноември","Декември"
  ];

  useEffect(() => {
    const saved = localStorage.getItem("students");
    if (saved) setStudents(JSON.parse(saved));
  }, []);

  useEffect(() => {
    localStorage.setItem("students", JSON.stringify(students));
  }, [students]);

  // Добавяне на ученици
  const handleAdd = () => {
    const entries = input.split(",").map(e => e.trim()).filter(e => e);
    const newStudents = entries.map(entry => {
      const [name, dates] = entry.split(":").map(e => e.trim());
      const dateList = dates ? dates.split("/").map(d => d.trim()) : [];
      return { name, dates: dateList, month };
    });
    setStudents([...students, ...newStudents]);
    setInput("");
  };

  const handleIncrement = index => {
    const updated = [...students];
    updated[index].dates.push(new Date().toLocaleDateString());
    setStudents(updated);
  };

  const handleDecrement = index => {
    const updated = [...students];
    if (updated[index].dates.length > 0) updated[index].dates.pop();
    setStudents(updated);
  };

  const handleDeleteAll = () => setStudents([]);

  const filteredStudents = students.filter(
    s => s.name.toLowerCase().includes(search.toLowerCase()) && s.month === month
  );

  const exportToExcel = () => {
    const workbook = XLSX.utils.book_new();
    const monthStudents = filteredStudents.map(s => ({
      Име: s.name,
      "Брой посещения": s.dates.length,
      Дати: s.dates.join(", "),
      "Дължими пари": s.dates.length * 10
    }));
    const ws = XLSX.utils.json_to_sheet(monthStudents);
    XLSX.utils.book_append_sheet(workbook, ws, month);
    const excelBuffer = XLSX.write(workbook, { bookType: "xlsx", type: "array" });
    const blob = new Blob([excelBuffer], { type: "application/octet-stream" });
    saveAs(blob, `Kickboxing_Attendance_${month}.xlsx`);
  };

  return (
    <div className="min-h-screen bg-green-900 flex flex-col items-center p-6 text-white">
      <h1 className="text-3xl font-bold mb-4 text-yellow-300 animate-pulse">
        Masaru Team Kickboxing Tracker
      </h1>

      <div className="mb-6 w-full max-w-lg space-y-2">
        <input
          type="text"
          placeholder="Име:дата1/дата2 (например Ivan:01-10/03-10)"
          value={input}
          onChange={e => setInput(e.target.value)}
          className="w-full p-2 border border-gray-400 rounded focus:outline-none focus:ring-2 focus:ring-yellow-300 text-black"
        />
        <button
          onClick={handleAdd}
          className="w-full bg-yellow-500 text-black py-2 rounded hover:bg-yellow-400 transform hover:scale-105 transition duration-200 shadow-lg"
        >
          Добави ученици
        </button>
        <button
          onClick={handleDeleteAll}
          className="w-full bg-red-600 text-white py-2 rounded hover:bg-red-500 transform hover:scale-105 transition duration-200 shadow-lg"
        >
          Изтрий всички
        </button>
        <input
          type="text"
          placeholder="Търси по име..."
          value={search}
          onChange={e => setSearch(e.target.value)}
          className="w-full p-2 border border-gray-400 rounded focus:outline-none focus:ring-2 focus:ring-yellow-300 text-black"
        />
        <select
          value={month}
          onChange={e => setMonth(e.target.value)}
          className="w-full p-2 border border-gray-400 rounded focus:outline-none focus:ring-2 focus:ring-yellow-300 text-black"
        >
          {months.map(m => (
            <option key={m} value={m}>{m}</option>
          ))}
        </select>
        <button
          onClick={exportToExcel}
          className="w-full bg-purple-600 text-white py-2 rounded hover:bg-purple-500 transform hover:scale-105 transition duration-200 shadow-lg"
        >
          Експорт в Excel
        </button>
      </div>

      <div className="w-full max-w-lg mb-4 text-right font-semibold text-lg">
        Общо за {month}: {filteredStudents.reduce((sum,s)=>sum+s.dates.length*10,0)} лв.
      </div>

      <div className="w-full max-w-lg space-y-2">
        {filteredStudents.map((student, index) => {
          const owes = student.dates.length > 0;
          return (
            <div
              key={index}
              className={`flex justify-between items-center p-3 rounded shadow-lg transition transform hover:scale-105
              ${owes ? 'bg-red-600 animate-pulse' : 'bg-green-700 animate-bounce'}`}
            >
              <div>
                <p className="font-semibold">{student.name}</p>
                <p className="text-sm">Дати: {student.dates.join(", ")}</p>
                <p className="text-sm">Дължими пари: {student.dates.length * 10} лв.</p>
              </div>
              <div className="flex space-x-2">
                <button
                  onClick={() => handleDecrement(index)}
                  className="bg-red-500 px-2 py-1 rounded hover:bg-red-400 transform hover:scale-110 transition duration-150"
                >-</button>
                <span className="px-2 font-bold">{student.dates.length}</span>
                <button
                  onClick={() => handleIncrement(index)}
                  className="bg-green-500 px-2 py-1 rounded hover:bg-green-400 transform hover:scale-110 transition duration-150"
                >+</button>
              </div>
            </div>
          );
        })}
      </div>

      <p className="mt-8 text-gray-200 text-sm">
        © Masaru Team | Made by Teodor Popovski, member of Masaru Team
      </p>
    </div>
  );
}
