// src/App.js
import React, { useState, useEffect } from 'react';
import * as XLSX from 'xlsx';
import { saveAs } from 'file-saver';
import './index.css';

export default function App() {
  const [input, setInput] = useState('');
  const [month, setMonth] = useState('Октомври');
  const [students, setStudents] = useState([]);
  const [search, setSearch] = useState('');
  const months = ['Януари','Февруари','Март','Април','Май','Юни','Юли','Август','Септември','Октомври','Ноември','Декември'];

  useEffect(() => {
    const saved = localStorage.getItem('students');
    if (saved) setStudents(JSON.parse(saved));
  }, []);

  useEffect(() => {
    localStorage.setItem('students', JSON.stringify(students));
  }, [students]);

  const handleAdd = () => {
    const names = input.split(',').map(name => name.trim()).filter(name => name);
    const newStudents = names.map(name => ({ name, classes: 0, month }));
    setStudents([...students, ...newStudents]);
    setInput('');
  };

  const handleIncrement = (index) => {
    const updated = [...students];
    updated[index].classes += 1;
    setStudents(updated);
  };

  const handleDecrement = (index) => {
    const updated = [...students];
    if (updated[index].classes > 0) updated[index].classes -= 1;
    setStudents(updated);
  };

  const handleDeleteAll = () => setStudents([]);

  const filteredStudents = students.filter(s => 
    s.name.toLowerCase().includes(search.toLowerCase()) && s.month === month
  );

  const totalAmount = filteredStudents.reduce((sum, s) => sum + s.classes * 10, 0);

  const exportAllMonthsToExcel = () => {
    const workbook = XLSX.utils.book_new();
    months.forEach(m => {
      const monthStudents = students.filter(s => s.month === m).map(s => ({
        Име: s.name,
        "Брой тренировки": s.classes,
        "Дължими пари": s.classes * 10
      }));
      if (monthStudents.length > 0) {
        const ws = XLSX.utils.json_to_sheet(monthStudents);
        XLSX.utils.book_append_sheet(workbook, ws, m);
      }
    });
    const excelBuffer = XLSX.write(workbook, { bookType: "xlsx", type: "array" });
    const blob = new Blob([excelBuffer], { type: "application/octet-stream" });
    saveAs(blob, `Kickboxing_Attendance_All_Months.xlsx`);
  };

  return (
    <div className="min-h-screen bg-gradient-to-b from-blue-100 to-purple-200 flex flex-col items-center p-6">
      <h1 className="text-3xl font-bold mb-4 text-blue-900 animate-pulse">Masaru Team Kickboxing Tracker</h1>

      <div className="mb-6 w-full max-w-lg space-y-2">
        <input
          type="text"
          placeholder="Въведи имена, разделени със запетая"
          value={input}
          onChange={(e) => setInput(e.target.value)}
          className="w-full p-2 border border-gray-400 rounded focus:outline-none focus:ring-2 focus:ring-blue-500"
        />
        <button
          onClick={handleAdd}
          className="w-full bg-blue-500 text-white py-2 rounded hover:bg-blue-600 transform hover:scale-105 transition duration-200 shadow-lg"
        >
          Добави ученици
        </button>
        <button
          onClick={handleDeleteAll}
          className="w-full bg-red-500 text-white py-2 rounded hover:bg-red-600 transform hover:scale-105 transition duration-200 shadow-lg"
        >
          Изтрий всички
        </button>
        <input
          type="text"
          placeholder="Търси по име..."
          value={search}
          onChange={(e) => setSearch(e.target.value)}
          className="w-full p-2 border border-gray-400 rounded focus:outline-none focus:ring-2 focus:ring-blue-500"
        />
        <select
          value={month}
          onChange={(e) => setMonth(e.target.value)}
          className="w-full p-2 border border-gray-400 rounded focus:outline-none focus:ring-2 focus:ring-blue-500"
        >
          {months.map(m => <option key={m} value={m}>{m}</option>)}
        </select>
        <button
          onClick={() => {
            const filtered = students.filter(s => s.month === month);
            if (filtered.length > 0) exportAllMonthsToExcel();
          }}
          className="w-full bg-green-500 text-white py-2 rounded hover:bg-green-600 transform hover:scale-105 transition duration-200 shadow-lg"
        >
          Експорт за текущия месец
        </button>
        <button
          onClick={exportAllMonthsToExcel}
          className="w-full bg-purple-500 text-white py-2 rounded hover:bg-purple-600 transform hover:scale-105 transition duration-200 shadow-lg"
        >
          Експорт за всички месеци
        </button>
      </div>

      <div className="w-full max-w-lg mb-4 text-right font-semibold text-lg">
        Обща сума за {month}: {totalAmount} лв.
      </div>

      <div className="w-full max-w-lg space-y-2">
        {filteredStudents.map((student, index) => {
          const hasDebt = student.classes > 0;
          return (
            <div
              key={index}
              className={`flex justify-between items-center p-3 rounded shadow-lg transition transform hover:scale-105
                ${hasDebt ? 'bg-red-300 animate-pulse' : 'bg-green-300 animate-bounce'}`}
            >
              <div>
                <p className="font-semibold">{student.name} ({student.month})</p>
                <p className="text-sm text-gray-700">Дължи: {student.classes * 10} лв.</p>
              </div>
              <div className="flex space-x-2">
                <button
                  onClick={() => handleDecrement(index)}
                  className="bg-red-500 text-white px-2 py-1 rounded hover:bg-red-600 transform hover:scale-110 transition duration-150 shadow-md"
                >-</button>
                <span className="px-2 font-bold">{student.classes}</span>
                <button
                  onClick={() => handleIncrement(index)}
                  className="bg-green-500 text-white px-2 py-1 rounded hover:bg-green-600 transform hover:scale-110 transition duration-150 shadow-md"
                >+</button>
              </div>
            </div>
          );
        })}
      </div>

      <p className="mt-8 text-gray-700 text-sm">
        © Masaru Team | Made by Teodor Popovski, member of Masaru Team
      </p>
    </div>
  );
}
