import React, { useState, useEffect } from "react";
import * as XLSX from "xlsx";
import { saveAs } from "file-saver";
import "./index.css";

export default function App() {
  const [input, setInput] = useState("");
  const [month, setMonth] = useState("–û–∫—Ç–æ–º–≤—Ä–∏");
  const [students, setStudents] = useState([]);
  const [search, setSearch] = useState("");
  const months = [
    "–Ø–Ω—É–∞—Ä–∏","–§–µ–≤—Ä—É–∞—Ä–∏","–ú–∞—Ä—Ç","–ê–ø—Ä–∏–ª","–ú–∞–π","–Æ–Ω–∏",
    "–Æ–ª–∏","–ê–≤–≥—É—Å—Ç","–°–µ–ø—Ç–µ–º–≤—Ä–∏","–û–∫—Ç–æ–º–≤—Ä–∏","–ù–æ–µ–º–≤—Ä–∏","–î–µ–∫–µ–º–≤—Ä–∏"
  ];

  useEffect(() => {
    const saved = localStorage.getItem("students");
    if (saved) {
      try {
        const parsed = JSON.parse(saved);
        const fixed = parsed.map(s => ({
          ...s,
          dates: Array.isArray(s.dates) ? s.dates : [],
          month: s.month || month,
        }));
        setStudents(fixed);
      } catch {
        setStudents([]);
      }
    }
  }, []);

  useEffect(() => {
    localStorage.setItem("students", JSON.stringify(students));
  }, [students]);

  const handleAdd = () => {
    const entries = input.split(",").map(e => e.trim()).filter(e => e);
    const newStudents = entries.map(entry => {
      const [name, dates] = entry.split(":").map(e => e.trim());
      const dateList = dates ? dates.split("/").map(d => d.trim()) : [];
      return { name, dates: dateList, month };
    });
    setStudents([...students, ...newStudents]);
    setInput("");
  };

  const handleIncrement = index => {
    const updated = [...students];
    if (!Array.isArray(updated[index].dates)) updated[index].dates = [];
    updated[index].dates.push(new Date().toLocaleDateString());
    setStudents(updated);
  };

  const handleDecrement = index => {
    const updated = [...students];
    if (Array.isArray(updated[index].dates) && updated[index].dates.length > 0) {
      updated[index].dates.pop();
    }
    setStudents(updated);
  };

  const handleDeleteAll = () => setStudents([]);

  const filteredStudents = students.filter(
    s => s.name.toLowerCase().includes(search.toLowerCase()) && s.month === month
  );

  const exportToExcel = () => {
    const workbook = XLSX.utils.book_new();
    const monthStudents = filteredStudents.map(s => ({
      –ò–º–µ: s.name,
      "–ë—Ä–æ–π –ø–æ—Å–µ—â–µ–Ω–∏—è": Array.isArray(s.dates) ? s.dates.length : 0,
      –î–∞—Ç–∏: Array.isArray(s.dates) ? s.dates.join(", ") : "",
      "–î—ä–ª–∂–∏–º–∏ –ø–∞—Ä–∏": (Array.isArray(s.dates) ? s.dates.length : 0) * 10
    }));
    const ws = XLSX.utils.json_to_sheet(monthStudents);
    XLSX.utils.book_append_sheet(workbook, ws, month);
    const excelBuffer = XLSX.write(workbook, { bookType: "xlsx", type: "array" });
    const blob = new Blob([excelBuffer], { type: "application/octet-stream" });
    saveAs(blob, `Masaru_Attendance_${month}.xlsx`);
  };

  const totalOwed = filteredStudents.reduce(
    (sum, s) => sum + ((Array.isArray(s.dates) ? s.dates.length : 0) * 10),
    0
  );

  return (
    <div className="min-h-screen bg-[#0b2e13] flex flex-col items-center p-6 text-white font-sans">
      <h1 className="text-4xl font-extrabold mb-6 text-yellow-400 drop-shadow-lg animate-pulse">
        ü•ä Masaru Team Kickboxing Tracker
      </h1>

      <div className="w-full max-w-xl bg-[#103a1a] p-5 rounded-2xl shadow-2xl mb-8 border border-green-700">
        <input
          type="text"
          placeholder="–ò–º–µ–Ω–∞, —Ä–∞–∑–¥–µ–ª–µ–Ω–∏ —Å—ä—Å –∑–∞–ø–µ—Ç–∞—è (–ø—Ä–∏–º–µ—Ä: –ò–≤–∞–Ω, –ú–∞—Ä–∏—è, –ü–µ—Ç—ä—Ä)"
          value={input}
          onChange={e => setInput(e.target.value)}
          className="w-full p-2 mb-3 border border-green-500 rounded-lg text-black focus:ring-2 focus:ring-yellow-400"
        />
        <button
          onClick={handleAdd}
          className="w-full bg-yellow-400 text-black py-2 rounded-lg font-semibold hover:bg-yellow-300 hover:scale-105 transform transition-all shadow-lg mb-2"
        >
          ‚ûï –î–æ–±–∞–≤–∏ —É—á–µ–Ω–∏—Ü–∏
        </button>
        <button
          onClick={handleDeleteAll}
          className="w-full bg-red-600 text-white py-2 rounded-lg font-semibold hover:bg-red-500 hover:scale-105 transform transition-all shadow-lg mb-2"
        >
          üóëÔ∏è –ò–∑—Ç—Ä–∏–π –≤—Å–∏—á–∫–∏
        </button>
        <input
          type="text"
          placeholder="üîç –¢—ä—Ä—Å–∏ –ø–æ –∏–º–µ..."
          value={search}
          onChange={e => setSearch(e.target.value)}
          className="w-full p-2 mb-3 border border-green-500 rounded-lg text-black focus:ring-2 focus:ring-yellow-400"
        />
        <select
          value={month}
          onChange={e => setMonth(e.target.value)}
          className="w-full p-2 mb-3 border border-green-500 rounded-lg text-black focus:ring-2 focus:ring-yellow-400"
        >
          {months.map(m => (
            <option key={m} value={m}>{m}</option>
          ))}
        </select>
        <button
          onClick={exportToExcel}
          className="w-full bg-purple-600 text-white py-2 rounded-lg font-semibold hover:bg-purple-500 hover:scale-105 transform transition-all shadow-lg"
        >
          üìÅ –ï–∫—Å–ø–æ—Ä—Ç –≤ Excel
        </button>
      </div>

      <div className="w-full max-w-xl mb-4 text-right font-semibold text-lg text-yellow-300">
        –û–±—â–æ –∑–∞ {month}: {totalOwed} –ª–≤.
      </div>

      <div className="grid gap-4 w-full max-w-xl">
        {filteredStudents.map((student, index) => (
          <div
            key={index}
            className="bg-[#14532d] p-4 rounded-2xl shadow-xl border border-green-600 hover:scale-105 transform transition-all hover:shadow-2xl"
          >
            <div className="flex justify-between items-center mb-2">
              <h2 className="text-xl font-bold text-yellow-400">
                {student.name}
              </h2>
              <div className="flex items-center space-x-2">
                <button
                  onClick={() => handleDecrement(index)}
                  className="bg-red-500 px-3 py-1 rounded-lg font-bold hover:bg-red-400 transition transform hover:scale-110"
                >‚àí</button>
                <span className="text-lg font-semibold">
                  {student.dates?.length || 0}
                </span>
                <button
                  onClick={() => handleIncrement(index)}
                  className="bg-green-500 px-3 py-1 rounded-lg font-bold hover:bg-green-400 transition transform hover:scale-110"
                >+</button>
              </div>
            </div>
            <p className="text-sm text-gray-200">
              <span className="font-semibold text-white">–î–∞—Ç–∏:</span>{" "}
              {student.dates?.length ? student.dates.join(", ") : "‚Äî"}
            </p>
            <p className="mt-1 font-semibold text-yellow-300">
              üí∞ –î—ä–ª–∂–∏–º–∏ –ø–∞—Ä–∏: {(student.dates?.length || 0) * 10} –ª–≤.
            </p>
          </div>
        ))}
      </div>

      <p className="mt-10 text-gray-400 text-sm italic">
        ¬© Masaru Team | Made by Teodor Popovski, member of Team Masaru
      </p>
    </div>
  );
}
